import React, { useState } from "react";
import Button from "../atoms/button";
import "./uploadModal.scss";

function UploadModal(params) {
  const [ state, setState ] = useState({})

  const closeUploadModal = () => {
    params.setUploadModalOn(false);
  };

  const fileData = () => {
    if (this.state.doc) {
      return (
        <p className="upload-details">
          Arquivo adicionado: {this.state.doc.name}
        </p>
      );
    } else {
      return (
        <p className="upload-details no-file">
          Nenhum arquivo adicionado - *utilizar modelo padr√£o*
        </p>
      );
    }
  };

  return (
    <>
      <div className="modal-background"></div>
      <section>
        <div>
          <span onClick={closeUploadModal}><p>X</p></span>
          <div className="content">
            <Button>Baixar planilha</Button>
            <Button onClick></Button>
            <div className="input-wrap">
              <input
                type="file"
                id="docUpload"
                name="docUpload"
                onChange={this.onFileChange}
              />
              <label for="docUpload">Subir planilha</label>
              {fileData()}
            </div>
          </div>
        </div>
      </section>
    </>
  );
}

export default UploadModal;
